package CleanCode;import java.util.Scanner;public class FizzBuzz {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Enter number = ");        int number = sc.nextInt();        String fizBuz = fizzBuzz(number);        System.out.print(fizBuz);    }    public static String fizzBuzz(int number){        boolean isFizz = number % 3 == 0;        boolean isBuzz = number % 5 == 0;        if(isFizz && isBuzz)            return "FizzBuzz";        if(isFizz)            return "Fizz";        if(isBuzz)            return "Buzz";        return number + "";    }}